mapper.select:
SELECT e.id AS id, e.content AS content, e.create_time AS createTime, e.parent_id AS parentId, e.content_type AS contentType, u.id AS userId, u.username AS username, (SELECT COUNT(*) FROM evaluations_upvote eu WHERE eu.user_id = #{userId} AND eu.evaluations_id = e.id) AS upvoteFlag, (SELECT COUNT(*) FROM evaluations_upvote eu WHERE eu.evaluations_id = e.id) AS upvoteCount, u.avatar AS avatar, u1.id AS replier_id, u1.username AS replierName, u1.avatar AS replierAvatar FROM evaluations e LEFT JOIN user u ON u.id = e.commenter_id LEFT JOIN user u1 ON u1.id = e.replier_id
AND e.content_type = #{contentType}
AND e.content like concat('%',#{content},'%')
AND e.create_time BETWEEN #{startTime} AND #{endTime}
ORDER BY e.create_time DESC
LIMIT #{current},#{size}

SELECT COUNT(*)
FROM evaluations e
WHERE e.content_id = #{contentId}
  AND e.content_type = #{contentType}
